import React,{useState} from 'react'
import './Signup.css'
import Form from 'react-bootstrap/Form'
import {Link,Redirect} from 'react-router-dom'
import {login} from '../actions/actions.auth'
import {connect} from 'react-redux'

// import Button from 'react-bootstrap/Button'
function Login({login}) {

    const {loginData, setLoginData} = useState({
        username: '',
        email: '',
        password: ''
    });

    const onChange = (e) => {
        setLoginData({...loginData,[e.target.name]:e.target.value})
    }

    const onSubmit = (e) => {
        e.preventDefault();
        login(loginData.username,loginData.email,loginData.password)
    }


    return (
        <div className ="div-from ">
            <Form onSubmit={(e) => onSubmit(e)}>
                <div className="container">
                <hr/>
                <h1 className = "h1sign">Log In</h1>
                <label for="username">Username</label>
                <input onChange= {(e) => onChange(e)} type="text" placeholder="Enter Username" name = "username" required></input>
                <label for="psw">Password<b/></label>
                <input onChange={(e) => onChange(e)} type = "password" placeholder="Enter Password" name = "psw" required></input>

                <div class="clearfix">
                    <button type="submit" class="signupbtn">Login</button>
                </div>
                <br />
                <h6>Don't have an account?<Link to ="/signup">Signup Now</Link> </h6>

                </div>
            </Form>
        </div>

    );
}

export default Login;

