import { 
    LOGIN_SUCCESS,LOGIN_FAILED,SIGNUP_FAILED,SIGNUP_SUCCESS,AUTHENTICATION_SUCCESS,AUTHENTICATION_FAILED,LOGOUT_USER
} from '../actions/actions.types'

const initialState = {
    access : localStorage.getItem('access'),
    isAuthenticated:null,
    user:null
};

export default function(state=initialState,action) {
    const {type, payload} = action;

    switch (type){
        case LOGIN_SUCCESS:
            localStorage.setItem('access',payload.token);
        return {
            ...state,
            isAuthenticated: true,
            acess : payload.token
        };


        case LOGIN_FAILED:
            case LOGOUT_USER:
            localStorage.removeItem('access',payload.token);
            return {
                ...state,
                isAuthenticated:false,
                user:null
            }

        default:
            return state;
    }
}
