import React, { useState } from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import { login } from "../actions/actions.auth";

const Login = ({ login,isAuthenticated }) => {
  const [loginData, SetLoginData] = useState({
    username: "",
    email: "",
    password: "",
  });

  const { username, email, password } = loginData;

  const onChange = (e) =>
    SetLoginData({ ...loginData, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(username, email, password);
    login(username, email, password);
  };

  if(isAuthenticated){
      return <Redirect to='/'/>
  }

  return (
    <div className = "div-form">
      <form className = "container" onSubmit={(e) => onSubmit(e)}>
      <h1 className = "loginh1">Log In</h1>
        <hr/>
        <input 
          type="text"
          placeholder="Your username here"
          username="username"
          autoComplete="on"
          onChange={(e) => onChange(e)}
        />
        <br />
        <input
          type="email"
          placeholder="Your email here"
          name="email"
          autoComplete="on"
          onChange={(e) => onChange(e)}
        />
        <br />
        <input
          type="password"
          placeholder="Your password here"
          name="password"
          autoComplete="on"
          onChange={(e) => onChange(e)}
        />
        <br />
        <button style = {{color:red}} className = "signupbtn" type="submit">Login</button>
         <h6>
        Dont have an account?<Link to="/signup">Create Account</Link>
      </h6>
      </form>
    </div>
  );
};

const mapDispatchtoProps = (state) => {
  return {
    isAuthenticated : state.auth.isAuthenticated,
  }
}


export default connect(mapDispatchtoProps, { login })(Login);
