import {
    LOGIN_FAILED,
    LOGIN_SUCCESS,
    AUTHENTICATION_SUCCESS,
    AUTHENTICATION_FAILED,
    SIGNUP_FAILED,
    SIGNUP_SUCCESS,
    LOGOUT_USER} from '../actions/actions.types';

const initialState = {
    access: localStorage.getItem('access'),
    isAuthenticated: null,
    user : null
}

export default function(state= initialState, action) {
    const {type,payload} = action;

    switch (type) {
        case LOGIN_SUCCESS:
            localStorage.setItem('accesss',payload.token)
        ;

        return {
            ...state,
            isAuthenticated: true,
            access: payload.token
        }

        case LOGIN_FAILED:
            case LOGOUT_USER:
                localStorage.removeItem('access');
                return {
                    ...state,
                    isAuthentication: false,
                    user: null
                }

        default:
            return state;
    }

}
